import re

#Limpar String do Trabalho

def limpar_string(string):
    nova_string = re.sub(r"(<[\w\s\=\"\-\/\;\:\.]*>)|(&\w*;)", " ", string)
    final_string = re.sub(r"\s{2,}", " ", nova_string)
    final_string1 = re.sub(r"(I{1,3}. )|(IV. )", "\n", final_string)
    final_string2 = re.sub(r"[a-zA-Zà-úÀ-Ú]* \d*\/\d* \- ", "", final_string1)
    print(f"\n{final_string2}\n")

